import {useState} from 'react'
import './App.css';
import React from 'react';

// part1 d

const App = () => {
  const [left, setLeft] = useState(0);
  const [right, setRight] = useState(0);

  return (
    <div>
      {left}
      <button onClick = {()=> setLeft(left + 1)}>left</button>
      <button onClick = {()=> setRight(right + 1)}>right</button>
      {right}
    </div>
  )
}




